# import hashlibimport hashlibimport randomimport stringimport secrets# ====================================== Generate random messages ==================================================def rnd_msg(length):    # https://www.geeksforgeeks.org/python-generate-random-string-of-given-length/    # chars = string.ascii_letters + string.digits + string.punctuation       # Random message Char. selection Opts.    chars = string.ascii_letters + string.digits                              # Random message Char. selection Opts.    rd_msg = "".join(random.choice(chars) for _ in range(length)).encode()    # Generating random strings    # .encode() is for converting the 'string' message into the 'bytes'    return rd_msg# ===================== Convert the object into binary string and write it into .txt file ===========================def write_into_file(msg, file_i):    hash_object = hashlib.sha256(msg)  # Create a hash object using message    # print(f"The digest for message {i} is =>", hash_object.hexdigest())    scale = 16    num_of_bits = 256    binary_digest = bin(int(hash_object.hexdigest(), scale))[2:].zfill(num_of_bits)    # print('The binary digest is =>', binary_digest)    with open(file_i, "w") as c:    # Open the i_th file for writing        c.write(binary_digest)      # Write the binary hash value to the file# ======================= Create 1000 seeds / messages and files with hashed binary values ===========================messages = []                               # Create an empty list to store all the random seeds / messagesfor i in range(1, 10):    rnd_input = secrets.randbelow(30)    message = rnd_msg(rnd_input)                   # Random message with 20 characters    messages.append(message)                # Save the message with every iteration    file_name = f"1hash_{i}.txt"             # file name selection based on the message    write_into_file(message, file_name)  # Write values into the file# ====================================== Create a list of messages ==================================================with open("List of messages.txt", "w") as f:    for index, message in enumerate(messages, start=1):  # Write the list of messages to the file with numbering        f.write(f"{index}. {message.decode()}\n")        # start=1 will start the iteration from index 1    print("The Entire Process is Successful!!")# ================================================= end =============================================================